  const [token, setToken] = useState('');
  const [trackUri, setTrackUri] = useState(''); // URI de la canción que se reproducirá

  // Obtener token de acceso de Spotify
  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.post('tu_endpoint_para_obtener_el_token');
      setToken(response.data.access_token);
    };

    fetchData();
  }, []);

  const handlePlay = () => {
    // Lógica para comenzar la reproducción
  };

  const handlePause = () => {
    // Lógica para pausar la reproducción
  };

  const handleTrackChange = (uri) => {
    // Lógica para cambiar la canción actual
    setTrackUri(uri);
  };

  return (
    <div>
      {/* Puedes agregar información sobre la canción actual, como título, artista, etc. */}
      <h3>Nombre de la canción - Artista</h3>

      {/* Componente de reproductor de Spotify */}
      <SpotifyPlayer
        token={token}
        uris={trackUri ? [trackUri] : []} // Puedes proporcionar un array de URIs de canciones para reproducir
        callback={(state) => {
          if (!state.isPlaying) {
            // Lógica para manejar la pausa de la canción
          }
        }}
        play={handlePlay}
        pause={handlePause}
        showSaveIcon
      />
    </div>
  );
};

export default Player;
