import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { WebPlaybackSDK } from 'react-spotify-web-playback-sdk';

const CLIENT_ID = "453d6c17658a4a4a8498f17cb1b22b75";
const CLIENT_SECRET = "6d243006672b46cea52da45f4fa7b0f7";

const Player = () => {
  const [token, setAccessToken] = useState('');
  const [trackUri, setTrackUri] = useState('');
  const [isPlaying, setIsPlaying] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.post('https://accounts.spotify.com/api/token', null, {
          params: {
            grant_type: 'client_credentials',
            client_id: CLIENT_ID,
            client_secret: CLIENT_SECRET,
          },
        });
        setAccessToken(response.data.access_token);
      } catch (error) {
        console.error('Error fetching Spotify access token:', error);
      }
    };

    fetchData();
  }, []); // Run only once on component mount

  const handlePlay = () => {
    setIsPlaying(true);
    // Lógica para comenzar la reproducción
  };

  const handlePause = () => {
    setIsPlaying(false);
    // Lógica para pausar la reproducción
  };

  const handleTrackChange = (uri) => {
    setTrackUri(uri);
  };

  const handlePlaySpecificSong = () => {
    const specificSongUri = 'spotify:track:4MjDJD8cW7iVeWInc2Bdy';
    handleTrackChange(specificSongUri);
    handlePlay();

    // Desplazar la página al elemento de audio al reproducir una canción específica
    audioRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div>
      <h3>Nombre de la canción - Artista</h3>
      <WebPlaybackSDK
        token={token}
        uris={trackUri ? [trackUri] : []}
        callback={(state) => {
          if (!state.isPlaying) {
            // Lógica para manejar la pausa de la canción
          }
        }}
        play={handlePlay}
        pause={handlePause}
        showSaveIcon
      />

      {/* Reproductor de audio HTML */}
      <audio controls>
        <source src={trackUri} type="audio/mp3" />
        Tu navegador no soporta el elemento de audio.
      </audio>

      <button onClick={handlePlaySpecificSong}>Reproducir canción específica</button>
    </div>
  );
};

export default Player;
