import React, { useState, useEffect } from 'react';
import SpotifyPlayer from 'react-spotify-web-playback';

const CLIENT_ID = "453d6c17658a4a4a8498f17cb1b22b75";
const CLIENT_SECRET = "6d243006672b46cea52da45f4fa7b0f7";

const Player = () => {
  const [token, setToken] = useState('');
  const [trackUri, setTrackUri] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://accounts.spotify.com/api/token', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded',
          },
          body: `grant_type=client_credentials&client_id=${CLIENT_ID}&client_secret=${CLIENT_SECRET}&scope=user-read-playback-state user-modify-playback-state`,
        });
  
        if (!response.ok) {
          throw new Error(`Error al obtener el token de acceso. Código: ${response.status}`);
        }
  
        const data = await response.json();
  
        if (!data.access_token) {
          throw new Error('Token de acceso no válido');
        }
  
        setToken(data.access_token);
      } catch (error) {
        console.error('Error fetching Spotify access token:', error.message);
      }
    };
  
    fetchData();
  }, []);



  const handlePlay = () => {
    // Lógica para comenzar la reproducción
    console.log('Reproduciendo...');
  };

  const handlePause = () => {
    // Lógica para pausar la reproducción
    console.log('Pausando...');
  };

  const handleTrackChange = (uri) => {
    // Lógica para cambiar la canción actual
    setTrackUri(uri);
  };

  return (
    <div>
      <h3>Nombre de la canción - Artista</h3>
      <SpotifyPlayer
        token={token}
        uris={trackUri ? [trackUri] : []}
        callback={(state) => {
          if (!state.isPlaying) {
            // Lógica para manejar la pausa de la canción
            console.log('Canción en pausa');
          }
        }}
        play={handlePlay}
        pause={handlePause}
        showSaveIcon
      />
    </div>
  );
};

export default Player;
