import React, { useState, useEffect } from "react";
import axios from "axios";

function DetalleCancion() {
  const [songInfo, setSongInfo] = useState(null);

  useEffect(() => {
    // Llamada a la API de Genius
    const accessToken = 'csaLJEuywJNmIV55H3mzlEz_LvdsNUQ4hXNFdCY7WY4U9lk19ThRn0379ytm69ro';
    const searchTerm = 'Bohemian Rhapsody';

    axios({
      method: 'get',
      url: `https://api.genius.com/search?q=${encodeURIComponent(searchTerm)}`,
      headers: {
        Authorization: `Bearer ${accessToken}`,
      },
    })
      .then(response => {
        const hits = response.data.response.hits;
        if (hits.length > 0) {
          const song = hits[0].result;
          setSongInfo(song);
        } else {
          console.log('Canción no encontrada.');
        }
      })
      .catch(error => {
        console.error('Error al realizar la solicitud a la API de Genius:', error);
      });
  }, []); // Asegúrate de ajustar las dependencias según sea necesario

  return (
    <div>
      <h2>Detalles de la canción</h2>
      {songInfo && (
        <div>
          <p>Título: {songInfo.title}</p>
          <p>Artista: {songInfo.primary_artist.name}</p>
          {/* Otros detalles que desees mostrar */}
        </div>
      )}
    </div>
  );
}

export default DetalleCancion;
