import React, { useEffect, useState } from 'react';
import SpotifyPlayer from 'react-spotify-web-playback';

const Player = ({ accessToken }) => {
    const [trackUri, setTrackUri] = useState('');

    useEffect(() => {
        // Recupera la informaci贸n de la canci贸n seleccionada desde el localStorage
        const selectedTrackInfo = localStorage.getItem('selectedTrack');

        if (selectedTrackInfo) {
            const { uri } = JSON.parse(selectedTrackInfo);
            setTrackUri(uri);
        }
    }, []);

    const handlePlay = ({ spotify_uri }) => {
        setTrackUri(spotify_uri);
    };

    return (
        <div>
            {accessToken && <SpotifyPlayer token={accessToken} uris={trackUri ? [trackUri] : []} />}
        </div>
    );
};

export default Player;
